function change_theme(button_){
	var headings = document.getElementsByClassName("heading");
	var p_tags = document.getElementsByTagName("p");
	var h3s = document.getElementsByTagName("h3");
	var h2s = document.getElementsByTagName("h2");
	var projects = document.getElementsByClassName("project");
	var li_tags = document.getElementsByTagName("li")
	var az_cert = document.getElementsByClassName("card");
	var buttons = document.getElementsByTagName("button")
	var cert_name = document.getElementsByClassName("cert-name");
	var cert_issuer = document.getElementsByClassName("cert-issuer");
	var cert_year = document.getElementsByClassName("cert-issuer");
	
	

	if (document.getElementById(button_).style.backgroundColor == "black"){
				document.getElementById("lead-content").getElementsByTagName("h1")[0].style.color = 'white'; //black
				document.getElementById("lead-overlay").style.backgroundColor = 'rgba(0,0,0,0.8)'; // 'rgba(255,255,255,0.85)'
				document.getElementById("menu").style.backgroundColor = "#3B3131";
				
				
				for (var i = 0; i < headings.length; i++) {
					headings[i].style.color = "white";
				}
				document.getElementById("about").style.backgroundColor = 'black';
				
				for (var i = 0; i < p_tags.length; i++) {
					p_tags[i].style.color = "white";
				}
				
				for (var i = 0; i < h3s.length; i++) {
					h3s[i].style.color = "white";
				}
				
				for (var i = 0; i < 2; i++) {
					h2s[i].style.color = "#B3B3B3";
				}
				
				document.getElementById("projects").style.backgroundColor = "#252525";
				
				for (var i = 0; i < projects.length; i++) {
					projects[i].style.backgroundColor = "black";
				}
				
				document.getElementById("skills").style.backgroundColor = "black";
				
				document.getElementById("cert").style.backgroundColor = "#252525";
				
				
				for (var i = 0; i < li_tags.length; i++) {
					if (li_tags[i].parentElement.id != "menu"){
						li_tags[i].style.backgroundColor = "#3E3E3E";
						li_tags[i].style.color = "white";
					}
				}
				
				document.querySelector("footer").style.backgroundColor = "black";
				
				jQuery('.bg-image').attr('src', 'images/cert_black.png');
				jQuery('#medium-logo').attr('src', 'images/medium_logo_white.png');
				jQuery('#github-logo').attr('src', 'images/github_logo_white.png');
				
				
				
				for (var i = 0; i < az_cert.length; i++) {
					az_cert[i].style.backgroundColor = "black";
				}
				
				for (var i = 0; i < buttons.length; i++) {
					buttons[i].style.color = "black";
				}
		
				
				document.getElementById("theme").style.backgroundColor = 'white';
				jQuery('#theme-icon').attr('src', 'images/light_mode.png');
				document.getElementById("theme").title = 'Light Theme';
				}
				
				
				
				
				
				
				else {
				document.getElementById("lead-content").getElementsByTagName("h1")[0].style.color = 'black';
				document.getElementById("lead-overlay").style.backgroundColor = 'rgba(255,255,255,0.85)';
				document.getElementById("menu").style.backgroundColor = "#ebebeb"; 
				
				for (var i = 0; i < headings.length; i++) {
					document.getElementsByClassName("heading")[i].style.color = "#374054";
				}
				document.getElementById("about").style.backgroundColor = 'white';
				
				for (var i = 0; i < p_tags.length; i++) {
					document.getElementsByTagName("p")[i].style.color = "#74808a";
				}
				
				for (var i = 0; i < h3s.length; i++) {
					h3s[i].style.color = "black";
				}
				
				for (var i = 0; i < 2; i++) {
					h2s[i].style.color = "#726E6D";
				}
				
				document.getElementById("projects").style.backgroundColor = "#f2f2f5";
				
				for (var i = 0; i < projects.length; i++) {
					projects[i].style.backgroundColor = "white";
				}
				
				document.getElementById("skills").style.backgroundColor = "white";
				
				document.getElementById("cert").style.backgroundColor = "#f2f2f5";
				
				for (var i = 0; i < li_tags.length; i++) {
					if (li_tags[i].parentElement.id != "menu"){
						li_tags[i].style.backgroundColor = "#e4e4ea";
						li_tags[i].style.color = "#374054";
					}
				}
				
				document.querySelector("footer").style.backgroundColor = "white";
				
				jQuery('.bg-image').attr('src', 'images/cert.png');
				jQuery('#medium-logo').attr('src', 'images/medium_logo.png');
				jQuery('#github-logo').attr('src', 'images/github_logo.png');
				
				
				
				for (var i = 0; i < az_cert.length; i++) {
					az_cert[i].style.backgroundColor = "white";
				}
				
				
				for (var i =0; i < cert_name.length; i++){
				document.getElementsByClassName("cert-name")[i].style.color = 'rgb(0,0,0)';
				document.getElementsByClassName("cert-issuer")[i].style.color = 'rgb(0,0,0)';
				document.getElementsByClassName("cert-year")[i].style.color = 'rgb(0,0,0)';
				}
				
				
				
				
				
				document.getElementById("theme").style.backgroundColor = 'black';
				jQuery('#theme-icon').attr('src', 'images/dark_mode.png');
				document.getElementById("theme").title = 'Dark Theme';
			}


}

function read_more(button, section, img, min_width, min_height, max_width, max_height){
	if (document.getElementById(button).innerHTML == "Read more"){
				document.getElementById(section).style.display = "block";
				document.getElementById(button).innerHTML = "Read less";
				document.getElementById(img).width = max_width;
				document.getElementById(img).height = max_height;
				}
				else {
				document.getElementById(section).style.display = "none";
				document.getElementById(button).innerHTML = "Read more";
				document.getElementById(img).width = min_width;
				document.getElementById(img).height = min_height;
				window.scrollBy(0, -10);
			}
}
